mapper.delete:
DELETE FROM bathhouse_info_like
WHERE bathhouse_info_id =#{bathhouseInfoId}
AND   member_id = #{memberId}

mapper.select:
SELECT COUNT(*) > 0
FROM bathhouse_info_like
WHERE bathhouse_info_id =#{bathhouseInfoId}
AND   member_id = #{memberId}

SELECT COUNT(*)
FROM bathhouse_info_like
WHERE bathhouse_info_id = #{bathhouseInfoId}
